INSERT INTO "wordbook" VALUES(1,'NULL','NULL','Affluenza','NULL','NULL','<h1>Affluenza</h1> </br>A blend of &apos;affluence&apos; and &apos;influenza&apos;. A social disease resulting from extreme materialism and excessive consumerism: earning more money and consuming more, which can lead to overwork, debt, stress, anxiety, etc.','dummy.mp3');
INSERT INTO "wordbook" VALUES(2,'NULL','NULL','Agritourism','NULL','NULL','<h1>Agritourism</h1> </br>A form of tourism in which tourists stay on farms or in agricultural villages, and often participate in farm activities.','dummy.mp3');
INSERT INTO "wordbook" VALUES(3,'NULL','NULL','Alcopop','NULL','NULL','<h1>Alcopop</h1> </br>Fruit drinks fortified with alcohol, designed and marketed to appeal to young people.','dummy.mp3');
INSERT INTO "wordbook" VALUES(4,'NULL','NULL','App','NULL','NULL','<h1>App</h1> </br>Abbreviation of &apos;application&apos;, software that performs a specific task.','dummy.mp3');
INSERT INTO "wordbook" VALUES(5,'NULL','NULL','Applepick','NULL','NULL','<h1>Applepick</h1> </br>Steal someone&apos;s iPhone.','dummy.mp3');
INSERT INTO "wordbook" VALUES(6,'NULL','NULL','Audiophile','NULL','NULL','<h1>Audiophile</h1> </br>Person who loves and collects high-quality audio equipment.','dummy.mp3');
INSERT INTO "wordbook" VALUES(7,'NULL','NULL','Baggravation','NULL','NULL','<h1>Baggravation</h1> </br>Blend of the words &apos;bag&apos; and &apos;aggravation&apos;. A feeling of annoyance and frustration at the airport when your baggage has not arrived but the other passengers&apos; bags have.','dummy.mp3');
INSERT INTO "wordbook" VALUES(8,'NULL','NULL','Blook','NULL','NULL','<h1>Blook</h1> </br>A blend of &apos;book&apos; and &apos;blog&apos; :  a book written by a blogger.','dummy.mp3');
INSERT INTO "wordbook" VALUES(9,'NULL','NULL','Breadcrumbing','NULL','NULL','<h1>Breadcrumbing</h1> </br>A navigation technique which helps users by displaying a list of links to the pages they have visited when exploring a website, for example:  home >>vocabulary>>transport.','dummy.mp3');
INSERT INTO "wordbook" VALUES(10,'NULL','NULL','Bromance','NULL','NULL','<h1>Bromance</h1> </br>Blend of &apos;brother&apos; and &apos;romance&apos;. A close non-sexual relationship between two men.','dummy.mp3');
INSERT INTO "wordbook" VALUES(11,'NULL','NULL','Burkini or Burquini','NULL','NULL','<h1>Burkini or Burquini</h1> </br>Blend of &apos;burqa&apos; and &apos;bikini&apos; A swimsuit worn by Muslim women which covers the whole body i.e. the arms to the wrist, the legs to the ankle, with a hood to cover the hair and neck.','dummy.mp3');
INSERT INTO "wordbook" VALUES(12,'NULL','NULL','Busking','NULL','NULL','<h1>Busking</h1> </br>Performing on the streets and other public places, while soliciting donations. Busy urban areas will attract street performers (buskers) who sing, play, juggle, etc','dummy.mp3');
INSERT INTO "wordbook" VALUES(13,'NULL','NULL','Buzz','NULL','NULL','<h1>Buzz</h1> </br>Excited interest or attention surrounding, for example, a new invention, a recent event or something that has become fashionable.','dummy.mp3');
INSERT INTO "wordbook" VALUES(14,'NULL','NULL','Buzzword','NULL','NULL','<h1>Buzzword</h1> </br>A new word or expression that is commonly used in specialized work environments or age-groups, and has become fashionable. Terms used in advertising can often convert into buzzwords and become widely used','dummy.mp3');
INSERT INTO "wordbook" VALUES(15,'NULL','NULL','Captcha','NULL','NULL','<h1>Captcha</h1> </br>Completely Automated Public Turing Test To Tell Computers and Humans Apart. A distorted image of letters and numbers used to ensure that a response is not generated by a computer, in order to prevent spamming.','dummy.mp3');
INSERT INTO "wordbook" VALUES(16,'NULL','NULL','Carjacking','NULL','NULL','<h1>Carjacking</h1> </br>Blend of &apos;car&apos; and &apos;hijacking&apos;. When a car driver is forced to give up his vehicle or drive to a destination designated by the attacker.','dummy.mp3');
INSERT INTO "wordbook" VALUES(17,'NULL','NULL','Chatroom','NULL','NULL','<h1>Chatroom</h1> </br>Areas on the internet where people can communicate by exchanging typed messages','dummy.mp3');
INSERT INTO "wordbook" VALUES(18,'NULL','NULL','Chick lit','NULL','NULL','<h1>Chick lit</h1> </br>Books, usually featuring female characters, written by women on contemporary themes and issues that appeal more to women than to men.','dummy.mp3');
INSERT INTO "wordbook" VALUES(19,'NULL','NULL','Chillaxing','NULL','NULL','<h1>Chillaxing</h1> </br>Blend of &apos;chilling&apos; and relaxing&apos;. Taking a break from stressful activities to rest or relax.','dummy.mp3');
INSERT INTO "wordbook" VALUES(20,'NULL','NULL','Citizen journalism','NULL','NULL','<h1>Citizen journalism</h1> </br>News collected and reported by ordinary people, especially through the use of blog software.','dummy.mp3');
INSERT INTO "wordbook" VALUES(21,'NULL','NULL','Click bait','NULL','NULL','<h1>Click bait</h1> </br>Put something on a website that will encourage visitors to click on a link.','dummy.mp3');
INSERT INTO "wordbook" VALUES(22,'NULL','NULL','Cloud computing','NULL','NULL','<h1>Cloud computing</h1> </br>A computing service which enables access to a shared pool of resources (servers, data storage, applications, etc.) over the Internet. Users can access and use tools or applications through a web browser without having to install them on their computers. ','dummy.mp3');
INSERT INTO "wordbook" VALUES(23,'NULL','NULL','Content farm','NULL','NULL','<h1>Content farm</h1> </br>A website that publishes large amounts of low-quality content, or content copied from elsewhere, in order to attract visitors and improve its search-engine rankings.','dummy.mp3');
INSERT INTO "wordbook" VALUES(24,'NULL','NULL','Copyleft','NULL','NULL','<h1>Copyleft</h1> </br>Opposite of copyright. Whereas copyright imposes restrictions on the distribution of a work or publication, copyleft eliminates restrictions and allows freedom of use for all.','dummy.mp3');
INSERT INTO "wordbook" VALUES(25,'NULL','NULL','Cosmeceutical','NULL','NULL','<h1>Cosmeceutical</h1> </br>A pharmaceutical product with beneficial effects on the skin e.g. anti-ageing creams containing a product that changes the cell biology.  ','dummy.mp3');
INSERT INTO "wordbook" VALUES(26,'NULL','NULL','Cot potato','NULL','NULL','<h1>Cot potato</h1> </br>Very young child who spends a lot of time watching television. (cot = a baby&apos;s bed)','dummy.mp3');
INSERT INTO "wordbook" VALUES(27,'NULL','NULL','Couch commerce','NULL','NULL','<h1>Couch commerce</h1> </br>Buying goods online from one&apos;s home.','dummy.mp3');
INSERT INTO "wordbook" VALUES(28,'NULL','NULL','Couch potato','NULL','NULL','<h1>Couch potato</h1> </br>A person who spends a lot of time sitting in front of the television.','dummy.mp3');
INSERT INTO "wordbook" VALUES(29,'NULL','NULL','Cronut','NULL','NULL','<h1>Cronut</h1> </br>A ring-shaped pastry that is a cross between a croissant and a doughnut.','dummy.mp3');
INSERT INTO "wordbook" VALUES(30,'NULL','NULL','Crowdfunding','NULL','NULL','<h1>Crowdfunding</h1> </br>Raising money for a project by getting a large number of people to make a small financial contribution, particularly by using a website.','dummy.mp3');
INSERT INTO "wordbook" VALUES(31,'NULL','NULL','Cyberbully','NULL','NULL','<h1>Cyberbully</h1> </br>A person who uses the Internet, electronic communication or social networks to harm, harass or intimidate another person.','dummy.mp3');
INSERT INTO "wordbook" VALUES(32,'NULL','NULL','Cyber cafe','NULL','NULL','<h1>Cyber cafe</h1> </br>A place which provides internet access to the public.','dummy.mp3');
INSERT INTO "wordbook" VALUES(33,'NULL','NULL','Cyberchondriac','NULL','NULL','<h1>Cyberchondriac</h1> </br>A person who imagines that he/she is suffering from an illness after reading about the symptoms on the Internet!','dummy.mp3');
INSERT INTO "wordbook" VALUES(34,'NULL','NULL','Cyberloaf','NULL','NULL','<h1>Cyberloaf</h1> </br>Spend time on the Internet at work doing personal things.','dummy.mp3');
INSERT INTO "wordbook" VALUES(35,'NULL','NULL','Cyberslacking','NULL','NULL','<h1>Cyberslacking</h1> </br>Using one&apos;s employer&apos;s Internet and email facilities for personal purposes during working hours.','dummy.mp3');
INSERT INTO "wordbook" VALUES(36,'NULL','NULL','Daycation','NULL','NULL','<h1>Daycation</h1> </br>A trip or short vacation which lasts only one day - daycationers do not stay away overnight. ','dummy.mp3');
INSERT INTO "wordbook" VALUES(37,'NULL','NULL','Decruitment','NULL','NULL','<h1>Decruitment</h1> </br>Euphemism for laying-off staff or downsizing a company.','dummy.mp3');
INSERT INTO "wordbook" VALUES(38,'NULL','NULL','Demitarian','NULL','NULL','<h1>Demitarian</h1> </br>A person who reduces by half their consumption of meat and animal products in order to reduce the environmental impact of their diet.','dummy.mp3');
INSERT INTO "wordbook" VALUES(39,'NULL','NULL','Dench','NULL','NULL','<h1>Dench</h1> </br>Extremely attractive, fashionable, impressive, etc','dummy.mp3');
INSERT INTO "wordbook" VALUES(40,'NULL','NULL','Docusoap','NULL','NULL','<h1>Docusoap</h1> </br>Blend of &apos;documentary&apos; and &apos;soap&apos;. (soap opera: sentimental TV serial) A &apos;docusoap&apos; is a reality television programme in the style of a documentary.','dummy.mp3');
INSERT INTO "wordbook" VALUES(41,'NULL','NULL','Dramedy','NULL','NULL','<h1>Dramedy</h1> </br>Combination of &apos;drama&apos; and &apos;comedy&apos;. A film, play or TV programme that mixes drama and comedy.','dummy.mp3');
INSERT INTO "wordbook" VALUES(42,'NULL','NULL','Droolworthy','NULL','NULL','<h1>Droolworthy</h1> </br>Something so attractive or exciting that it makes you want to have it e.g. a droolworthy dress, a droolworthy cake, etc.','dummy.mp3');
INSERT INTO "wordbook" VALUES(43,'NULL','NULL','Drug driving','NULL','NULL','<h1>Drug driving</h1> </br>Driving a vehicle while under the influence of drugs.','dummy.mp3');
INSERT INTO "wordbook" VALUES(44,'NULL','NULL','Dwell time','NULL','NULL','<h1>Dwell time</h1> </br>The length of time spent on a website.','dummy.mp3');
INSERT INTO "wordbook" VALUES(45,'NULL','NULL','Earworm','NULL','NULL','<h1>Earworm</h1> </br>A tune that keeps repeating itself over and over again in our heads.','dummy.mp3');
INSERT INTO "wordbook" VALUES(46,'NULL','NULL','E-cruitment','NULL','NULL','<h1>E-cruitment</h1> </br>Online recruitment of employees, including online submission of resumes and cover letters','dummy.mp3');
INSERT INTO "wordbook" VALUES(47,'NULL','NULL','Elancer','NULL','NULL','<h1>Elancer</h1> </br>A professional person who works from home and provides services on the internet.','dummy.mp3');
INSERT INTO "wordbook" VALUES(48,'NULL','NULL','Emoji','NULL','NULL','<h1>Emoji</h1> </br>Icon used in electronic messages and webpages.','dummy.mp3');
INSERT INTO "wordbook" VALUES(49,'NULL','NULL','Emoticon','NULL','NULL','<h1>Emoticon</h1> </br>A blend of &apos;emotion&apos; and &apos;icon&apos;. A symbol, used in email messages, which is made out of punctuation marks and resembles a human face.','dummy.mp3');
INSERT INTO "wordbook" VALUES(50,'NULL','NULL','E-piracy','NULL','NULL','<h1>E-piracy</h1> </br>Electronic piracy Illegal downloading of material found on the internet (films, music, etc.)','dummy.mp3');
INSERT INTO "wordbook" VALUES(51,'NULL','NULL','E-quaintance','NULL','NULL','<h1>E-quaintance</h1> </br>A person you know only through online networks.','dummy.mp3');
INSERT INTO "wordbook" VALUES(52,'NULL','NULL','E-stalk','NULL','NULL','<h1>E-stalk</h1> </br>To stalk (follow) someone using Internet searches and email.','dummy.mp3');
INSERT INTO "wordbook" VALUES(53,'NULL','NULL','EV','NULL','NULL','<h1>EV</h1> </br>Electric vehicle, a vehicle which runs entirely on electricity stored in rechargeable batteries.','dummy.mp3');
INSERT INTO "wordbook" VALUES(54,'NULL','NULL','E-waste','NULL','NULL','<h1>E-waste</h1> </br>Electronic material and devices that have been thrown away.','dummy.mp3');
INSERT INTO "wordbook" VALUES(55,'NULL','NULL','Face Time','NULL','NULL','<h1>Face Time</h1> </br>Application which enables people to make video phone calls. They can speak and see each other at the same time. ','dummy.mp3');
INSERT INTO "wordbook" VALUES(56,'NULL','NULL','Fandom','NULL','NULL','<h1>Fandom</h1> </br>Blend of’ fan’ and the suffix ‘-dom’, (as in &apos;kingdom&apos;). The fans of a particular person, team, etc. regarded collectively as a community. ','dummy.mp3');
INSERT INTO "wordbook" VALUES(57,'NULL','NULL','Fashionista','NULL','NULL','<h1>Fashionista</h1> </br>Person who dresses according to the latest fashion trends.','dummy.mp3');
INSERT INTO "wordbook" VALUES(58,'NULL','NULL','Favicon','NULL','NULL','<h1>Favicon</h1> </br>Favourite icon: a tiny little graphic that appears when you bookmark a site.','dummy.mp3');
INSERT INTO "wordbook" VALUES(59,'NULL','NULL','Finlit / fin lit','NULL','NULL','<h1>Finlit / fin lit</h1> </br>Financial literacy. Knowledge and understanding of financial language and issues related to finance.','dummy.mp3');
INSERT INTO "wordbook" VALUES(60,'NULL','NULL','Flame war','NULL','NULL','<h1>Flame war</h1> </br>A period during which angry or rude email messages are exchanged.','dummy.mp3');
INSERT INTO "wordbook" VALUES(61,'NULL','NULL','Flash mob','NULL','NULL','<h1>Flash mob</h1> </br>A crowd that gathers in a pre-determined place, performs an action then disperses very quickly. The mob is not told exactly what to do until just before the event.','dummy.mp3');
INSERT INTO "wordbook" VALUES(62,'NULL','NULL','Flexitarian','NULL','NULL','<h1>Flexitarian</h1> </br>A vegetarian who sometimes eats meat or fish.','dummy.mp3');
INSERT INTO "wordbook" VALUES(63,'NULL','NULL','Freemale','NULL','NULL','<h1>Freemale</h1> </br>A woman who is happy to stay single and independent so that she can do what she wants when she wants.','dummy.mp3');
INSERT INTO "wordbook" VALUES(64,'NULL','NULL','Flightmare','NULL','NULL','<h1>Flightmare</h1> </br>Blend of &apos;flight&apos; and &apos;nightmare&apos;. Unpleasant air travel experience (lost luggage, missed connections, etc.)','dummy.mp3');
INSERT INTO "wordbook" VALUES(65,'NULL','NULL','Flog','NULL','NULL','<h1>Flog</h1> </br>A fake blog.  A blog (online journal) which appears to have been written by an independent person but has in fact been created by a company or business in order to advertise a product or service.','dummy.mp3');
INSERT INTO "wordbook" VALUES(66,'NULL','NULL','Floordrobe','NULL','NULL','<h1>Floordrobe</h1> </br>Blend of &apos;floor&apos; and &apos;wardrobe&apos;. A pile of clothes dropped on the floor.','dummy.mp3');
INSERT INTO "wordbook" VALUES(67,'NULL','NULL','Foodoir','NULL','NULL','<h1>Foodoir</h1> </br>A blend of &apos;food&apos; and &apos;memoir&apos;. An account of someone&apos;s life or personal experiences, with a strong emphasis on food, often including recipes and cookery advice.','dummy.mp3');
INSERT INTO "wordbook" VALUES(68,'NULL','NULL','Fracking','NULL','NULL','<h1>Fracking</h1> </br>A method of extracting oil or natural gas from rock formations deep below the earth&apos;s surface, by drilling and creating cracks.','dummy.mp3');
INSERT INTO "wordbook" VALUES(69,'NULL','NULL','Frape','NULL','NULL','<h1>Frape</h1> </br>Blend of ‘Facebook’ and ‘rape’. Making changes to someone’s Facebook pages without their knowledge or permission.','dummy.mp3');
INSERT INTO "wordbook" VALUES(70,'NULL','NULL','Frenemy','NULL','NULL','<h1>Frenemy</h1> </br>An enemy who pretends to be your friend.','dummy.mp3');
INSERT INTO "wordbook" VALUES(71,'NULL','NULL','Frostjack','NULL','NULL','<h1>Frostjack</h1> </br>A blend of the words &apos;frost&apos; and &apos;hijack&apos;.','dummy.mp3');
INSERT INTO "wordbook" VALUES(72,'NULL','NULL','Frostjacking','NULL','NULL','<h1>Frostjacking</h1> </br>Stealing a car on a cold day when the owner leaves the engine running to defrost the windows.','dummy.mp3');
INSERT INTO "wordbook" VALUES(73,'NULL','NULL','Gastropub','NULL','NULL','<h1>Gastropub</h1> </br>A pub which, in addition to beer and alcoholic drinks, offers gastronomic cuisine.','dummy.mp3');
INSERT INTO "wordbook" VALUES(74,'NULL','NULL','Gastrosexuals','NULL','NULL','<h1>Gastrosexuals</h1> </br>A new generation of men who see cooking more as a hobby than  a household chore, and use their cooking skills to impress friends and potential partners.','dummy.mp3');
INSERT INTO "wordbook" VALUES(75,'NULL','NULL','Geek','NULL','NULL','<h1>Geek</h1> </br>A person obsessively concentrated on a particular interest, especially new technological devices.','dummy.mp3');
INSERT INTO "wordbook" VALUES(76,'NULL','NULL','Glad rags','NULL','NULL','<h1>Glad rags</h1> </br>Your best clothes or dressy clothes you wear on special occasions.','dummy.mp3');
INSERT INTO "wordbook" VALUES(77,'NULL','NULL','Glamping','NULL','NULL','<h1>Glamping</h1> </br>Blend of &apos;glamour&apos; and &apos;camping&apos;. Luxury camping : sleeping in the open but with every comfort including cooking equipment.','dummy.mp3');
INSERT INTO "wordbook" VALUES(78,'NULL','NULL','Glass cliff','NULL','NULL','<h1>Glass cliff</h1> </br>Refers to a situation where women are selected for positions when there is a strong likelihood of failure.','dummy.mp3');
INSERT INTO "wordbook" VALUES(79,'NULL','NULL','Gran-lit','NULL','NULL','<h1>Gran-lit</h1> </br>Blend of &apos;granny&apos; and &apos;literature&apos;. Books that appeal more to older people.','dummy.mp3');
INSERT INTO "wordbook" VALUES(80,'NULL','NULL','Greycation','NULL','NULL','<h1>Greycation</h1> </br>Going on holiday or vacation with grandparents in order to reduce the cost.','dummy.mp3');
INSERT INTO "wordbook" VALUES(81,'NULL','NULL','Guesstimate','NULL','NULL','<h1>Guesstimate</h1> </br>Blend of &apos;guess&apos; and &apos;estimate&apos;. A rough estimate without any claim of accuracy.','dummy.mp3');
INSERT INTO "wordbook" VALUES(82,'NULL','NULL','Hacktivist','NULL','NULL','<h1>Hacktivist</h1> </br>A person who manipulates information on the internet in order to transmit a message, usually political.','dummy.mp3');
INSERT INTO "wordbook" VALUES(83,'NULL','NULL','Hashtag','NULL','NULL','<h1>Hashtag</h1> </br>The # symbol (hashtag) is used on social networking services such as Twitter to identify a keyword or topic of interest and search for messages (tweets) related to the subject.','dummy.mp3');
INSERT INTO "wordbook" VALUES(84,'NULL','NULL','Hater','NULL','NULL','<h1>Hater</h1> </br>A social networking app which allows people to share their dislikes.','dummy.mp3');
INSERT INTO "wordbook" VALUES(85,'NULL','NULL','Haycation','NULL','NULL','<h1>Haycation</h1> </br>A holiday or vacation spent on a farm.','dummy.mp3');
INSERT INTO "wordbook" VALUES(86,'NULL','NULL','HENRY','NULL','NULL','<h1>HENRY</h1> </br>Abbreviation of high earner not rich yet; refers to a person with an income between $100,000 and $250,000.','dummy.mp3');
INSERT INTO "wordbook" VALUES(87,'NULL','NULL','Hoody or hoodie','NULL','NULL','<h1>Hoody or hoodie</h1> </br>A person, especially a youth, wearing a hooded top.','dummy.mp3');
INSERT INTO "wordbook" VALUES(88,'NULL','NULL','Hotspot','NULL','NULL','<h1>Hotspot</h1> </br>Location in which wireless Internet access is available for example airports, hotels, train stations, etc.','dummy.mp3');
INSERT INTO "wordbook" VALUES(89,'NULL','NULL','Humblebrag','NULL','NULL','<h1>Humblebrag</h1> </br>To say something with apparent modesty but at the same time actually boast about an achievement.','dummy.mp3');
INSERT INTO "wordbook" VALUES(90,'NULL','NULL','Hyperconnected','NULL','NULL','<h1>Hyperconnected</h1> </br>To be able to communicate and be instantly reachable through multiple devices with Internet connectivity (smartphones, laptops, tablets, etc.).','dummy.mp3');
INSERT INTO "wordbook" VALUES(91,'NULL','NULL','ICE number','NULL','NULL','<h1>ICE number</h1> </br>In Case of Emergency ','dummy.mp3');
INSERT INTO "wordbook" VALUES(92,'NULL','NULL','Impactful','NULL','NULL','<h1>Impactful</h1> </br>Having a great impact or effect, or making a strong impression.','dummy.mp3');
INSERT INTO "wordbook" VALUES(93,'NULL','NULL','Infomania','NULL','NULL','<h1>Infomania</h1> </br>Constantly checking and responding to email and text messages.','dummy.mp3');
INSERT INTO "wordbook" VALUES(94,'NULL','NULL','Infomercial','NULL','NULL','<h1>Infomercial</h1> </br>Blend of ’information’ and ‘commercial’. A short sponsored film, usually on television, which promotes a product in an instructive way. ','dummy.mp3');
INSERT INTO "wordbook" VALUES(95,'NULL','NULL','Jigsaw family','NULL','NULL','<h1>Jigsaw family</h1> </br>A family which includes the children from each partner&apos;s previous relationship(s), in addition to any children they may have together.','dummy.mp3');
INSERT INTO "wordbook" VALUES(96,'NULL','NULL','Jumbrella','NULL','NULL','<h1>Jumbrella</h1> </br>Blend of &apos;jumbo&apos; and &apos;umbrella&apos;. Very large umbrella set above tables outdoors at a coffee shop, pub or restaurant.','dummy.mp3');
INSERT INTO "wordbook" VALUES(97,'NULL','NULL','Landline','NULL','NULL','<h1>Landline</h1> </br>A telephone connected to wires in a fixed location as opposed to ','dummy.mp3');
INSERT INTO "wordbook" VALUES(98,'NULL','NULL','Laymanise/laymanize','NULL','NULL','<h1>Laymanise/laymanize</h1> </br>To simplify technical information so that it can be understood by ordinary people or non-specialists.','dummy.mp3');
INSERT INTO "wordbook" VALUES(99,'NULL','NULL','Legsie','NULL','NULL','<h1>Legsie</h1> </br>A photograph taken by yourself of your suntanned legs to show that you are enjoying your holiday. (The sand and sea are usually visible in the picture.)','dummy.mp3');
INSERT INTO "wordbook" VALUES(100,'NULL','NULL','Locavore','NULL','NULL','<h1>Locavore</h1> </br>A person who only eats food produced locally.','dummy.mp3');
INSERT INTO "wordbook" VALUES(101,'NULL','NULL','Loom band','NULL','NULL','<h1>Loom band</h1> </br>A small bracelet made from brightly-coloured rubber bands which are woven together in a variety of configurations.','dummy.mp3');
INSERT INTO "wordbook" VALUES(102,'NULL','NULL','Mailbomb','NULL','NULL','<h1>Mailbomb</h1> </br>To deliberately try to disrupt another computer system by sending massive amounts of email to it.','dummy.mp3');
INSERT INTO "wordbook" VALUES(103,'NULL','NULL','Me time','NULL','NULL','<h1>Me time</h1> </br>A period of time spent exclusively on yourself doing something that you enjoy and allows you to relax.','dummy.mp3');
INSERT INTO "wordbook" VALUES(104,'NULL','NULL','Netiquette','NULL','NULL','<h1>Netiquette</h1> </br>Blend of &apos;network&apos; and &apos;etiquette&apos;. Set of rules governing appropriate behaviour and courtesy on the internet.','dummy.mp3');
INSERT INTO "wordbook" VALUES(105,'NULL','NULL','Netizen','NULL','NULL','<h1>Netizen</h1> </br>Blend of &apos;internet&apos; and &apos;citizen&apos;. A person who spends an excessive amount of time on the internet.','dummy.mp3');
INSERT INTO "wordbook" VALUES(106,'NULL','NULL','Netpicker','NULL','NULL','<h1>Netpicker</h1> </br>A person who surfs the internet looking for information in order to impress others with their knowledge of current events.','dummy.mp3');
INSERT INTO "wordbook" VALUES(107,'NULL','NULL','Nevertiree','NULL','NULL','<h1>Nevertiree</h1> </br>A person who continues to work after they have reached the age of retirement.','dummy.mp3');
INSERT INTO "wordbook" VALUES(108,'NULL','NULL','Newbie','NULL','NULL','<h1>Newbie</h1> </br>A new member of any group, community, or activity.','dummy.mp3');
INSERT INTO "wordbook" VALUES(109,'NULL','NULL','Nonfrontational','NULL','NULL','<h1>Nonfrontational</h1> </br>The act of avoiding conflict or confrontation.','dummy.mp3');
INSERT INTO "wordbook" VALUES(110,'NULL','NULL','Nonliner','NULL','NULL','<h1>Nonliner</h1> </br>Someone who rarely or never uses the Internet, usually because they cannot access it.','dummy.mp3');
INSERT INTO "wordbook" VALUES(111,'NULL','NULL','Noogler','NULL','NULL','<h1>Noogler</h1> </br>New Google employee','dummy.mp3');
INSERT INTO "wordbook" VALUES(112,'NULL','NULL','Notspot','NULL','NULL','<h1>Notspot</h1> </br>An area where there is slow Internet access or no connection at all.','dummy.mp3');
INSERT INTO "wordbook" VALUES(113,'NULL','NULL','Noughties','NULL','NULL','<h1>Noughties</h1> </br>The years between 2000 and 2009 which contain a &apos;nought&apos; (zero), in the same way as other decades are called the &apos;thirties&apos;, &apos;sixties&apos;, etc .','dummy.mp3');
INSERT INTO "wordbook" VALUES(114,'NULL','NULL','Offshorable','NULL','NULL','<h1>Offshorable</h1> </br>Something that can be done or produced in another country, especially at reduced costs.','dummy.mp3');
INSERT INTO "wordbook" VALUES(115,'NULL','NULL','OH','NULL','NULL','<h1>OH</h1> </br>Abbreviation of &apos;other half&apos;, a person&apos;s wife, husband or partner.','dummy.mp3');
INSERT INTO "wordbook" VALUES(116,'NULL','NULL','Omnishambles','NULL','NULL','<h1>Omnishambles</h1> </br>A totally mismanaged situation characterized by numerous blunders and miscalculations.','dummy.mp3');
INSERT INTO "wordbook" VALUES(117,'NULL','NULL','Onesie','NULL','NULL','<h1>Onesie</h1> </br>A one-piece garment for adults, rather like a baby’s sleeping suit, usually made of soft material and worn for relaxing or sleeping.','dummy.mp3');
INSERT INTO "wordbook" VALUES(118,'NULL','NULL','Optics','NULL','NULL','<h1>Optics</h1> </br>The way a situation appears to the general public, or the impression it gives.','dummy.mp3');
INSERT INTO "wordbook" VALUES(119,'NULL','NULL','Outernet','NULL','NULL','<h1>Outernet</h1> </br>Traditional media (newspapers, magazines, radio, television) as opposed to the internet.','dummy.mp3');
INSERT INTO "wordbook" VALUES(120,'NULL','NULL','Overparenting','NULL','NULL','<h1>Overparenting</h1> </br>Being excessively protective of one&apos;s children in order to guarantee their safety and ensure their success in life.','dummy.mp3');
INSERT INTO "wordbook" VALUES(121,'NULL','NULL','Oversharing','NULL','NULL','<h1>Oversharing</h1> </br>Providing too much information on the Internet (credit card details, personal information, etc.) especially through social network sites.','dummy.mp3');
INSERT INTO "wordbook" VALUES(122,'NULL','NULL','Overworking class','NULL','NULL','<h1>Overworking class</h1> </br>A segment of today&apos;s working population where there is a desire or need to work long hours.','dummy.mp3');
INSERT INTO "wordbook" VALUES(123,'NULL','NULL','Password fatigue','NULL','NULL','<h1>Password fatigue</h1> </br>Being tired of having to remember a large number of passwords for different electronic devises.','dummy.mp3');
INSERT INTO "wordbook" VALUES(124,'NULL','NULL','Password wallet','NULL','NULL','<h1>Password wallet</h1> </br>An online service where a person’s passwords can be stored and managed.','dummy.mp3');
INSERT INTO "wordbook" VALUES(125,'NULL','NULL','Paywall','NULL','NULL','<h1>Paywall</h1> </br>A system which restricts access to a website, or certain webpage content, to users who have paid a subscription.','dummy.mp3');
INSERT INTO "wordbook" VALUES(126,'NULL','NULL','Photobomb','NULL','NULL','<h1>Photobomb</h1> </br>Intrude into the background of a photograph just before it is taken.','dummy.mp3');
INSERT INTO "wordbook" VALUES(127,'NULL','NULL','Quintastic','NULL','NULL','<h1>Quintastic</h1> </br>A blend of the adjective &apos;fantastic&apos; and the prefix &apos;quin&apos;. An informal way of referring to a person who is aged fifty or over but remains sexy, smart, energetic and successful, especially someone famous. In August 2011 President Barack Obama became a quintastic.','dummy.mp3');
INSERT INTO "wordbook" VALUES(128,'NULL','NULL','Recessionista','NULL','NULL','<h1>Recessionista</h1> </br>Popular new term for a person who succeeds in dressing stylishly on a tight budget.','dummy.mp3');
INSERT INTO "wordbook" VALUES(129,'NULL','NULL','Ringtone','NULL','NULL','<h1>Ringtone</h1> </br>The sound made by a mobile or cell phone to indicate an incoming call.','dummy.mp3');
INSERT INTO "wordbook" VALUES(130,'NULL','NULL','Road rage','NULL','NULL','<h1>Road rage</h1> </br>Aggressive habits often resulting in violence against other drivers.','dummy.mp3');
INSERT INTO "wordbook" VALUES(131,'NULL','NULL','Robocall','NULL','NULL','<h1>Robocall</h1> </br>Commercial telemarketing call which plays a recorded message.','dummy.mp3');
INSERT INTO "wordbook" VALUES(132,'NULL','NULL','Sandwich generation','NULL','NULL','<h1>Sandwich generation</h1> </br>A generation of middle-aged people who have to care both for their  children and their elderly parents.','dummy.mp3');
INSERT INTO "wordbook" VALUES(133,'NULL','NULL','Screenager','NULL','NULL','<h1>Screenager</h1> </br>A young person or teenager who spends a lot of time in front of the computer screen.','dummy.mp3');
INSERT INTO "wordbook" VALUES(134,'NULL','NULL','Selfie','NULL','NULL','<h1>Selfie</h1> </br>A photograph that one has taken of oneself (for example a photograph taken with a smartphone).','dummy.mp3');
INSERT INTO "wordbook" VALUES(135,'NULL','NULL','Shabby-chic','NULL','NULL','<h1>Shabby-chic</h1> </br>Cottage-style decor achieved by using worn or “distressed” furniture and neutral-coloured fabrics, or new items suitably treated to appear old and look comfortable. The result is an elegant overall effect.','dummy.mp3');
INSERT INTO "wordbook" VALUES(136,'NULL','NULL','Sindie','NULL','NULL','<h1>Sindie</h1> </br>Single income now divorced.','dummy.mp3');
INSERT INTO "wordbook" VALUES(137,'NULL','NULL','Sitcom','NULL','NULL','<h1>Sitcom</h1> </br>Blend of &apos;situation&apos; and &apos;comedy&apos;. A drama, on television, based on humorous everyday situations.','dummy.mp3');
INSERT INTO "wordbook" VALUES(138,'NULL','NULL','Site scraper','NULL','NULL','<h1>Site scraper</h1> </br>Software that collects content from other sites (without permission).','dummy.mp3');
INSERT INTO "wordbook" VALUES(139,'NULL','NULL','Slumdog','NULL','NULL','<h1>Slumdog</h1> </br>Very poor, underprivileged person who lives in an overcrowded squalid area of a city called a slum','dummy.mp3');
INSERT INTO "wordbook" VALUES(140,'NULL','NULL','Smartwatch','NULL','NULL','<h1>Smartwatch</h1> </br>A watch that can be used as a phone or a computer, with a small keyboard.','dummy.mp3');
INSERT INTO "wordbook" VALUES(141,'NULL','NULL','Smirt','NULL','NULL','<h1>Smirt</h1> </br>Blend of &apos;smoke&apos; and &apos;flirt&apos; Smoking prohibition laws have led to a new sort of social pastime : &apos;smirting&apos;, smokers getting to know each other when outside on the pavement.','dummy.mp3');
INSERT INTO "wordbook" VALUES(142,'NULL','NULL','Snail mail','NULL','NULL','<h1>Snail mail</h1> </br>The standard system of delivering mail which is very slow in contrast to electronic mail.','dummy.mp3');
INSERT INTO "wordbook" VALUES(143,'NULL','NULL','Social supermarkets','NULL','NULL','<h1>Social supermarkets</h1> </br>Supermarkets that offer discounted food exclusively to those in poverty. The food is donated by retailers and manufacturers. The food is cheap because it may be mislabelled, have damaged packaging or be nearing an expiration date, but it is still edible.','dummy.mp3');
INSERT INTO "wordbook" VALUES(144,'NULL','NULL','Sofalise/sofalize','NULL','NULL','<h1>Sofalise/sofalize</h1> </br>Stay home and use the internet, phone or other electronic device to communicate with people (social networking, chatting, tweeting, etc.), rather than go out and meet them face to face.','dummy.mp3');
INSERT INTO "wordbook" VALUES(145,'NULL','NULL','Solopreneur','NULL','NULL','<h1>Solopreneur</h1> </br>A person who is the owner of their business and runs it alone.','dummy.mp3');
INSERT INTO "wordbook" VALUES(146,'NULL','NULL','Tech neck','NULL','NULL','<h1>Tech neck</h1> </br>Wrinkles around the neck caused by looking down at phones, tablets, etc.','dummy.mp3');
INSERT INTO "wordbook" VALUES(147,'NULL','NULL','Textonym','NULL','NULL','<h1>Textonym</h1> </br>A word produced by predictive text software when you press a  combination of numbers on the keypad of a mobile phone.','dummy.mp3');
INSERT INTO "wordbook" VALUES(148,'NULL','NULL','Textspeak','NULL','NULL','<h1>Textspeak</h1> </br>Language used in text messages consisting of abbreviations, acronyms, initials, emoticons, etc.','dummy.mp3');
INSERT INTO "wordbook" VALUES(149,'NULL','NULL','Textual harassment','NULL','NULL','<h1>Textual harassment</h1> </br>Sending text messages to mobile phones which insult or abuse people.','dummy.mp3');
INSERT INTO "wordbook" VALUES(150,'NULL','NULL','Tombstoning','NULL','NULL','<h1>Tombstoning</h1> </br>Jumping or diving into water from a dangerously high place such as a hotel balcony, a cliff, bridge, wall, roof, etc.','dummy.mp3');
INSERT INTO "wordbook" VALUES(151,'NULL','NULL','Trashion','NULL','NULL','<h1>Trashion</h1> </br>Blend of &apos;trash&apos; and &apos;fashion&apos;. Fashionable items created from old, used and recycled elements.','dummy.mp3');
INSERT INTO "wordbook" VALUES(152,'NULL','NULL','Trekkie','NULL','NULL','<h1>Trekkie</h1> </br>A fan of the TV science fiction series Star Trek.','dummy.mp3');
INSERT INTO "wordbook" VALUES(153,'NULL','NULL','Tribal marketing','NULL','NULL','<h1>Tribal marketing</h1> </br>Marketing strategy which consists in using the social behaviour of certain groups (&apos;tribes&apos;) of consumers (e.g. surfers, rappers) to promote a product or service. Very often used by clothing and accessory brands.','dummy.mp3');
INSERT INTO "wordbook" VALUES(154,'NULL','NULL','Tweet','NULL','NULL','<h1>Tweet</h1> </br>People who &apos;tweet&apos; send short messages via the microblogging service Twitter.','dummy.mp3');
INSERT INTO "wordbook" VALUES(155,'NULL','NULL','Tweetheart','NULL','NULL','<h1>Tweetheart</h1> </br>A user of the Twitter service who is very popular or admired, or with whom other users communicate a lot.','dummy.mp3');
INSERT INTO "wordbook" VALUES(156,'NULL','NULL','Tweet seats','NULL','NULL','<h1>Tweet seats</h1> </br>Seats in a theatre or concert hall given to people who wish to tweet during the performance.','dummy.mp3');
INSERT INTO "wordbook" VALUES(157,'NULL','NULL','Twittion','NULL','NULL','<h1>Twittion</h1> </br>Blend of &apos;Twitter&apos; and &apos;petition&apos;,  a Twitter petition.','dummy.mp3');
INSERT INTO "wordbook" VALUES(158,'NULL','NULL','Trolling','NULL','NULL','<h1>Trolling</h1> </br>Being provocatively rude or insulting on the Internet in order to cause anger or conflict in an online community such as a blog, chat room or discussion forum.','dummy.mp3');
INSERT INTO "wordbook" VALUES(159,'NULL','NULL','Unfollow','NULL','NULL','<h1>Unfollow</h1> </br>Stop receiving another&apos;s person&apos;s messages on Twitter.','dummy.mp3');
INSERT INTO "wordbook" VALUES(160,'NULL','NULL','Unfriend','NULL','NULL','<h1>Unfriend</h1> </br>Remove someone from your list of friends on social networks such as Facebook.','dummy.mp3');
INSERT INTO "wordbook" VALUES(161,'NULL','NULL','Upskill','NULL','NULL','<h1>Upskill</h1> </br>Teach an employee new or additional skills.','dummy.mp3');
INSERT INTO "wordbook" VALUES(162,'NULL','NULL','UX','NULL','NULL','<h1>UX</h1> </br>Short for &apos;user experience&apos;.','dummy.mp3');
INSERT INTO "wordbook" VALUES(163,'NULL','NULL','Videophile','NULL','NULL','<h1>Videophile</h1> </br>Person who is very interested in watching videos and making recordings, and values high-quality results.','dummy.mp3');
INSERT INTO "wordbook" VALUES(164,'NULL','NULL','Viral marketing','NULL','NULL','<h1>Viral marketing</h1> </br>Marketing strategy that consists in encouraging people to pass along information to friends, family and colleagues through e-mail messages, blogs, video-sharing, etc., so that the marketing message spreads like a virus.','dummy.mp3');
INSERT INTO "wordbook" VALUES(165,'NULL','NULL','Vook','NULL','NULL','<h1>Vook</h1> </br>A combination of video, text, images and social streams in an electronic book.','dummy.mp3');
INSERT INTO "wordbook" VALUES(166,'NULL','NULL','Web rage','NULL','NULL','<h1>Web rage</h1> </br>Anger or frustration as a result of difficulties or problems encountered when using the Internet.','dummy.mp3');
INSERT INTO "wordbook" VALUES(167,'NULL','NULL','Webinar','NULL','NULL','<h1>Webinar</h1> </br>Presentation or seminar conducted over the web.','dummy.mp3');
INSERT INTO "wordbook" VALUES(168,'NULL','NULL','Weblish','NULL','NULL','<h1>Weblish</h1> </br>A form of English that is used on the web (use of abbreviations, acronyms, small letters, absence of punctuation and hyphens etc.) Also known as : webspeak, netspeak, internetese.','dummy.mp3');
INSERT INTO "wordbook" VALUES(169,'NULL','NULL','Widget','NULL','NULL','<h1>Widget</h1> </br>Blend of &apos;window&apos; and &apos;gadget&apos;. A small application or tool that can be installed and executed within a web page.','dummy.mp3');
INSERT INTO "wordbook" VALUES(170,'NULL','NULL','Wiki','NULL','NULL','<h1>Wiki</h1> </br>A web page which allows multiple users to contribute to its content. The most well-known is the website Wikipedia.','dummy.mp3');
INSERT INTO "wordbook" VALUES(171,'NULL','NULL','Winterval','NULL','NULL','<h1>Winterval</h1> </br>Blend of &apos;winter&apos; and &apos;festival&apos;. A festival that takes place in winter.','dummy.mp3');
INSERT INTO "wordbook" VALUES(172,'NULL','NULL','Wordle','NULL','NULL','<h1>Wordle</h1> </br>The words of a piece of text arranged into a sort of graphic. The more frequent a particular word appears in the text, the bigger its size in the wordle. (Also called &apos;word cloud&apos; or &apos;text cloud&apos;.)','dummy.mp3');
INSERT INTO "wordbook" VALUES(173,'NULL','NULL','Zumba','NULL','NULL','<h1>Zumba</h1> </br>A fitness activity which combines lively international music with dance exercises.','dummy.mp3');
